#include "includes/TCP_PW.hpp"

#include <iostream>

int main(int argc, char const *argv[]) {

    TCP_PW *tcp = new TCP_PW(TCP_PW_CLIENT);

    tcp -> start(argc, argv);
    // tcp -> connectA();

    int op;

    while(1){
        std::cout << "\t\t\tTCP_PW Menu\n\n";
        std::cout << "Escolha uma das opcoes abaixo:\n";
        std::cout << "1 - Conectar ao servidor\n";
        std::cout << "2 - Enviar uma mensagem\n";
        std::cout << "3 - Enviar uma mensagem aleatoria de tamanho k\n";
        std::cout << "4 - Finalizar conexao\n";
        std::cout << "-1 - Sair\n";
        std::cout << "> ";
        std::cin >> op;
        std::cin.ignore();
        if(op == -1){
            break;
        } else if(op == 1){
            tcp -> connectA();
        } else if(op == 2){
            std::string msg;
            std::cout << "Digite a mensagem:\n> ";
            getline(std::cin, msg);
            tcp -> sendMsg(msg.c_str());
        } else if(op == 3){
            std::string msg = "";
            std::cout << "Digite o tamanho da string:\n> ";
            int x;
            std::cin >> x;
            srand(time(NULL));
            for(int i = 0; i < x; i++){
                msg += (rand() % 26 + 'A');
            }
            tcp -> sendMsg(msg.c_str());
        } else if(op == 4){
            tcp -> disconnect();
        } else if(op == 20){
            std::string msg = "";
            for(int i=0; i<20; i++){
                msg += " % ";
                msg += " % ";
                msg+= " % ";
            }
            tcp->sendMsg(msg.c_str());
        }
    }

    return 0;
}
